// --- CUSTOM CURSOR ---
const cursor = document.querySelector('.custom-cursor');
let mx=0, my=0, cx=0, cy=0;
const lerp = parseFloat(getComputedStyle(document.documentElement).getPropertyValue('--ease-factor'));
document.addEventListener('mousemove', e => { mx=e.clientX; my=e.clientY; });
(function animate() {
  cx += (mx - cx)*lerp; cy += (my - cy)*lerp;
  cursor.style.left = cx+'px'; cursor.style.top = cy+'px';
  requestAnimationFrame(animate);
})();
document.addEventListener('mouseover', e => {
  if (e.target.closest('.card, a, button, .btn, #to-top')) {
    cursor.classList.add('active');
  }
});
document.addEventListener('mouseout', e => {
  if (e.target.closest('.card, a, button, .btn, #to-top')) {
    cursor.classList.remove('active','arrow-left','arrow-right');
  }
});
document.addEventListener('mousedown', () => cursor.classList.add('pressed'));
document.addEventListener('mouseup', () => cursor.classList.remove('pressed'));

// --- THEME & HEADER SHADOW ---
const themeBtns = document.querySelectorAll('#theme-toggle, #theme-toggle-mobile');
const bodyEl = document.body;
const headerEl = document.getElementById('main-header');
themeBtns.forEach(btn => btn.addEventListener('click', () => {
  bodyEl.dataset.theme = bodyEl.dataset.theme==='dark'?'light':'dark';
  btn.classList.toggle('rotate');
}));
window.addEventListener('scroll', () => {
  headerEl.classList.toggle('scrolled', window.scrollY>10);
});

// --- NAVIGATION, PROGRESS, DOTS, TO-TOP ---
document.querySelectorAll('nav a, .btn, #section-dots button').forEach(el => {
  el.addEventListener('click', e => {
    e.preventDefault();
    const tgt = el.getAttribute('href')?.slice(1) || el.dataset.target;
    document.getElementById(tgt).scrollIntoView({behavior:'smooth'});
    // close mobile nav if open
    document.getElementById('mobile-nav').classList.remove('open');
  });
});
const progress = document.getElementById('progress');
const dots = document.querySelectorAll('#section-dots button');
const secs = document.querySelectorAll('section, #home');
const toTop = document.getElementById('to-top');
window.addEventListener('scroll', () => {
  const y = window.scrollY, dh = document.body.scrollHeight - window.innerHeight;
  progress.style.width = (y/dh*100)+'%';
  toTop.style.display = y>400?'flex':'none';
  secs.forEach((s,i) => {
    if (y >= s.offsetTop-100 && y < s.offsetTop+s.offsetHeight-100) {
      dots.forEach(d=>d.classList.remove('active'));
      dots[i].classList.add('active');
      document.querySelectorAll('nav a').forEach(a=>a.classList.remove('active'));
      document.querySelector(`nav a[href="#${s.id}"]`)?.classList.add('active');
    }
  });
});
toTop.addEventListener('click', ()=> window.scrollTo({top:0,behavior:'smooth'}));

// --- TYPEWRITER ---
const typeEl = document.getElementById('typewriter');
const texts = ['Proyekto ng Graviton 2027 sa Filipino 4'];
let ti=0, ci=0;
function type() {
  if (ci<texts[ti].length) {
    typeEl.textContent += texts[ti][ci++];
    setTimeout(type,70);
  } else {
    setTimeout(()=>{
      typeEl.textContent=''; ci=0; ti=(ti+1)%texts.length; type();
    },99000);
  }
}
setTimeout(type,1000);

// --- SECTION REVEAL ---
const obs = new IntersectionObserver(entries => {
  entries.forEach(e => e.isIntersecting && e.target.classList.add('revealed'));
},{threshold:.2});
document.querySelectorAll('section, #home').forEach(s=>obs.observe(s));

// --- BUILD CAROUSELS ---
function buildCarousel(id) {
  const cont    = document.getElementById(id);
  const essays  = carouselData[id] || [];
  essays.forEach(essay => {
    const c = document.createElement('div');
    c.className = 'card';
    c.innerHTML = `
      <div class="card-front">
        <h3>${essay.title}</h3>
        <span class="author">${essay.author}</span>
        <p>${essay.snippet}</p>
      </div>`;

    // decide which opener to use:
    if (id === 'carousel3' && essay.trackSrc && essay.trackName) {
      c.addEventListener('click', () => 
        openMusicReading(
          essay.title,
          essay.author,
          essay.snippet,
          essay.trackSrc,
          essay.trackName
        )
      );
    } else {
      c.addEventListener('click', () => 
        openReading(essay.title, essay.author, essay.snippet)
      );
    }

    cont.appendChild(c);
  });

}
['carousel1','carousel2','carousel3'].forEach(buildCarousel);

// --- READING OVERLAY ---
const rv = document.getElementById('reading-view');
const rt = document.getElementById('read-title');
const ra = document.getElementById('read-author');
const rtxt = document.getElementById('read-text');
const closeBtn = document.getElementById('close-reading');
let scrollPosition = 0;

function openReading(title, author, text) {
  // 1. Record scroll
  scrollPosition = window.scrollY || window.pageYOffset;

  // 2. Freeze background
  document.body.classList.add('body--fixed');
  // shift it up so the viewport stays where it was
  document.body.style.top = `-${scrollPosition}px`;

  // 3. Show your overlay
  rt.textContent = title;
  ra.textContent = `Isinulat ni: ${author}`;
  rtxt.innerHTML = text;
  rv.classList.add('open');
  bodyEl.classList.add('disable-scroll');
}

function closeReading() {
  // 1. Hide overlay
  rv.classList.remove('open');
  bodyEl.classList.remove('disable-scroll');

  // 2. Unfreeze background
  document.body.classList.remove('body--fixed');
  document.body.style.top = '';

  // 3. Temporarily disable smooth scroll on <html>
  const html = document.documentElement;
  const previousScrollBehavior = html.style.scrollBehavior;
  html.style.scrollBehavior = 'auto';

  // 4. Restore scroll instantly
  window.scrollTo(0, scrollPosition);

  // 5. Restore smooth scroll (optional)
  html.style.scrollBehavior = previousScrollBehavior || '';
}

closeBtn.addEventListener('click', closeReading);
document.addEventListener('keydown', e => e.key==='Escape' && closeReading());

