// ——— Music Player Elements ———
const playerEl    = document.getElementById('music-player');
const toggleBtn   = document.getElementById('player-toggle');
const audioEl     = document.getElementById('player-audio');
const progressBar = document.getElementById('progress-bar');
const progCont    = document.getElementById('progress-container');
const timeEl      = document.getElementById('time-elapsed');
const trackTitle  = document.getElementById('track-title');

// ——— 1. NEW: openMusicReading ———
// (title, author, text, trackSrc, trackName)
function openMusicReading(title, author, text, trackSrc, trackName) {
  // Open reading overlay
  openReading(title, author, text);

  // Configure mini-player
  audioEl.src = trackSrc;
  trackTitle.textContent = trackName;
  progressBar.style.width = '0%';
  timeEl.textContent = '0:00';
  toggleBtn.textContent = '▶️';

  playerEl.classList.remove('hidden');
}

// ——— 2. NEW: closeMusicReading ———
function closeMusicReading() {
  audioEl.pause();
  playerEl.classList.add('hidden');
  closeReading();
}

// ——— Play/pause button ———
toggleBtn.addEventListener('click', () => {
  if (audioEl.paused) {
    audioEl.play();
    toggleBtn.textContent = '⏸️';
  } else {
    audioEl.pause();
    toggleBtn.textContent = '▶️';
  }
});

// ——— Update progress bar and time ———
audioEl.addEventListener('timeupdate', () => {
  const pct = (audioEl.currentTime / audioEl.duration) * 100 || 0;
  progressBar.style.width = `${pct}%`;

  const mins = Math.floor(audioEl.currentTime / 60);
  const secs = String(Math.floor(audioEl.currentTime % 60)).padStart(2, '0');
  timeEl.textContent = `${mins}:${secs}`;
});

// ——— Seek on progress bar click ———
progCont.addEventListener('click', e => {
  const rect = progCont.getBoundingClientRect();
  const clickX = e.clientX - rect.left;
  const ratio = clickX / rect.width;
  audioEl.currentTime = ratio * audioEl.duration;
});

// ——— Close button ———
document.getElementById('close-reading').addEventListener('click', () => {
  if (!playerEl.classList.contains('hidden')) {
    closeMusicReading();
  } else {
    closeReading();
  }
});


document.addEventListener('DOMContentLoaded', () => {
  document.querySelectorAll('.carousel').forEach(carousel => {
    let isDown = false;
    let startX = 0;
    let scrollLeft = 0;
    let moved = false;
    let suppressClick = false;
    let velocity = 0;
    let lastX = 0;
    let lastTime = 0;
    let momentumFrame;

    function startDrag(e) {
      cancelMomentum(); // stop any existing momentum
      isDown = true;
      startX = e.clientX;
      lastX = startX;
      scrollLeft = carousel.scrollLeft;
      moved = false;
      velocity = 0;
      lastTime = performance.now();
      carousel.classList.add('active');
      document.body.style.userSelect = 'none';
    }

    function onDrag(e) {
      if (!isDown) return;
      const x = e.clientX;
      const now = performance.now();
      const dx = x - lastX;
      const dt = now - lastTime;

      // prevent tiny dt spikes
      if (dt > 0 && dt < 100) {
        velocity = dx / dt; // px/ms
      }

      const totalDx = x - startX;
      if (Math.abs(totalDx) > 5) moved = true;
      carousel.scrollLeft = scrollLeft - totalDx;

      lastX = x;
      lastTime = now;
    }

    function endDrag() {
      if (isDown && moved) {
        suppressClick = true;
        setTimeout(() => suppressClick = false, 0);
        startMomentumScroll();
      }

      isDown = false;
      carousel.classList.remove('active');
      document.body.style.userSelect = '';
    }

    function cancelMomentum() {
      if (momentumFrame) cancelAnimationFrame(momentumFrame);
      momentumFrame = null;
    }

    function startMomentumScroll() {
      cancelMomentum();
      const decay = 0.95; // friction
      const minVelocity = 0.02; // px/ms threshold to stop

      function momentum() {
        carousel.scrollLeft -= velocity * 16; // assume ~16ms per frame (60fps)

        velocity *= decay;

        if (Math.abs(velocity) > minVelocity) {
          momentumFrame = requestAnimationFrame(momentum);
        } else {
          cancelMomentum();
        }
      }

      momentum();
    }

    carousel.addEventListener('pointerdown', startDrag, { passive: true });
    window.addEventListener('pointermove', onDrag, { passive: false });
    window.addEventListener('pointerup', endDrag);
    window.addEventListener('pointerleave', endDrag);

    carousel.querySelectorAll('.card').forEach(card => {
      card.addEventListener('click', e => {
        if (suppressClick) {
          e.stopImmediatePropagation();
          e.preventDefault();
        }
      }, true);
    });
  });
});
